class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        vector<int>pref(n,0);
        for(int i=0;i<n;i++)
        {
            if(i==0)
            {
                pref[i]=colors[i];
            }
            else
            {
                pref[i]=pref[i-1]+colors[i];
            }
        }
        // 0 1 1 0 1
        vector<int>x(n,0);
        for(int i=1;i<n-1;i++)
        {
            if(colors[i-1]!=colors[i]&&colors[i-1]==colors[i+1])
            {
                x[i]=1;
            }
        }  
        for(int i=1;i<n;i++)
        x[i]+=x[i-1];


         int ans=0;
        for(int i=0;i<n;i++)
        {
            int rem=n-i;
            if(k<=rem)
            {
                int r=i+k-1;
                // if(colors[r]==colors[i])
                {
                     int sum=x[r-1];
                     sum-=x[i];
                    //  cout<<i<<" "<<sum<<"\n";
                        if(sum==k-2)
                         ans++;

                }
            }
            // 0 1 0 1 0
            // 0 1 2 3 4
            else 
            {
                int asked= k-rem;
                int ff=asked-1;
                // if(colors[i]==colors[ff])
                {
                    int sum=x[n-1]-x[i];
                    if(i<n-1)
                    {
                        if(colors[n-1]!=colors[n-2]&&colors[n-2]==colors[0])
                        sum++;
                    }
                    if(ff-1>=0)
                    sum+=x[ff-1];
                    if(ff>0)
                    {
                        if(colors[0]!=colors[n-1]&&colors[1]!=colors[0])
                        sum++;
                    }
                    // cout<<i<<" "<<sum<<"\n";
                    if(sum==k-2)
                        ans++;
                }
            }
        }
        return ans;
    }
};
