class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) 
{
    int ans{0};
    int n = colors.size();
    int x{0},y{1},z,p;
    while (1)
    {
        if (y<x)
        {
            p = y+n-x+1;
        }
        else
        {
            p = y-x+1;
        }
        z = y-1;
        if (z<0)
        {
            z = n-1;
        }
        if (p<k)
        {   
            if (colors[y]==colors[z])
            {
                if (y<x)
                {
                    break;
                }
                x = y;
            }
            y++;
            if (y==n)
            {
                y = 0;
            }

        }
        else
        {
            if (colors[y]!=colors[z])
            {
                ans++;
            }
            x++;
        }
        if (x==n)
        {
            break;
        }

    }

    return ans;

}
};
