class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        colors += colors
        ans = i = 0
        while i < n:
            j = i + 1
            while j < 2*n - 1 and colors[j - 1] != colors[j]:
                if j + 1 - k >= n:
                    return ans
                elif j + 1 - k >= i:
                    ans += 1
                j += 1
            i = j
        return ans
