class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        m = len(colors)
        colors = colors + colors
        equalPairs = 0
        ans = 0
        for end in range(1, m + k - 1):
            equalPairs += int(colors[end] == colors[end - 1])
            if end >= k - 1:
                ans += int(equalPairs == 0)
                equalPairs -= int(colors[end - k + 1] == colors[end - k + 2])
        return ans
