class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        colors.extend(colors[:k-1])
        s = 0
        ans = 0
        for i in range(len(colors)-1):
            if colors[i] != colors[i+1]:
                s += 1
            else:
                s = 0
            if s >= k-1:
                ans += 1
        return ans
