class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans=0;
        int l=0;
        vector<int> v;
        for(int i=0;i<colors.size();i+=1)
        {
            v.push_back(colors[i]);
        }
        for(int i=0;i<k-1;i+=1)
        {
            v.push_back(colors[i]);
        }
        for(int i=1;i<v.size();i+=1)
        {
            if(v[i]==v[i-1])
            {
                if(i-l>=k) ans+=(i-l)-k+1;
                l=i;
                printf("%d %d %d\n",l,i,ans);
            }
        }
        //printf("%d %d %d\n",l,ans,v.size());
        if(v.size()-l>=k) ans+=(v.size()-l)-k+1;
        return ans;
    }
};
