class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int i = 0;
        for(int j = 0; j < k-1; j++)
        {
            colors.push_back(colors[i]);
            i++;
        }
        vector<int> v = {-1};
        for(int i = 0; i < n + k - 2; i++)
        {
            if(colors[i] == colors[i+1])
            {
                v.push_back(i);
            }
        }
        v.push_back(n+k-2);
        int ans = 0;
        for(int i = 1; i < v.size(); i++)
        {
            //cout<<v[i]<<" ";
            if(v[i] - v[i-1] >= k)
            {
                ans += (v[i] - v[i-1] - k + 1);
            }
        }
        return ans;
    }
};
