class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        for(int i=0;i<k-1;i++){
            v.push_back(v[i]);
        }
        int c=-1;
        int ans=0,n=v.size();
        // for(auto &el: v){
        //     cout<<el<<" ";
        // }
        for(int i=0,j=1;j<n;j++){
            if(j-i+1<k){
                if(!(v[j]!=v[j-1])){
                    c=j;
                }
            }
            else{
                if(v[j]!=v[j-1]){
                    if(i>=c){
                        ans++;
                    }
                    i++;
                }
                else{
                    c=j;
                    i++;
                }
            }
            // cout<<c<<" ";
        }
        // cout<<endl;
        return ans;
    }
};
