class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ng = 0; // Number of groups
        int st = 0; // Start index
        int end = 1; // End index
        int n = colors.size(); // Size of the colors array

        while (end < n + k && st < n) {
            int temp = end % n;
            while (colors[temp] != colors[(temp - 1 + n) % n] && end < n + k - 1) {
                end++;
                temp = end % n;
            }
            ng += max(end - st - k + 1, 0);
            st = end;
            end = st + 1;
        }

        return ng;
    }
};
