class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int ans=0;
        int  n=arr.size();
        for(int i=0;i<k;i++){
            arr.push_back(arr[i]);
        }

        vector<int>ind;
        int i=0,j=0;
        while(i<arr.size()-k){
            j=i;
            int flag=arr[j];
            while(j<arr.size() && arr[j]==flag){
                j++;
                flag=1-flag;
            }
            if(j<=n){
                // ind.push_back(j-i);
                if(j-i>=k)ans+=j-i-k+1;
            }
            else{
                // ind.push_back(n-i);
                if(i+j-i-k<n && j-i+1-k>=0)ans+=j-i+1-k;
                else if(j-i-k>=0)ans+=j-i-k;
            }
            i=j;
        }

        // for(auto i:ind){
        //     cout<<i<<" ";
        //     if(i>=k)
        //     ans+=i-k+1;

        //     if(i==arr.size())ans--;
        // }



        return ans;

    }
};
