class Solution:
    def __init__(self):
        self.arr = [0] * 300001
        self.pd = [0] * 300001

    def numberOfAlternatingGroups(self, colors, k):
        cd = 1
        ls = colors[0]
        count = 0
        length = len(colors)

        for i in range(1, length + 1):
            self.arr[i] = colors[i - 1]
            self.arr[i + length] = self.arr[i]

        for i in range(1, length * 2 + 1):
            if self.arr[i] != ls:
                ls = self.arr[i]
                cd += 1
            else:
                ls = self.arr[i]
                cd = 1

            if cd >= k:
                if i <= length:
                    count += 1
                    self.pd[i] = 1
                elif self.pd[i - length] == 0:
                    count += 1

        return count
