class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& A, int k) {
        int n = A.size(), las = A.back();
        for(int &x: A) {
            swap(las, x);
            x ^= las;
        }
        int p = 0;
        for( ; p < n && A[p]; ++p);
        if(p == n)
            return n;
        rotate(A.begin(), A.begin() + p, A.end());
        int ans = 0;
        for(int i = 0, j; i < n; ) {
            for(j = i; i < n && A[i] == A[j]; ++i);
            if(A[j] && i - j >= k - 1)
                ans += i - j - k + 2;
        }
        return ans;
    }
};
