class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n=len(colors)
        def create(i):
            for j in range(i,i+k-1):
                if colors[j%n]==colors[(j+1)%n]:
                    return i+1
            #print(i,i+k-1,"....")
            return -1
        valid=0
        cnt=0
        i=0
        while i<n:
            if valid==0:
                r=create(i)
                if r==-1:
                    i+=1
                    cnt+=1
                    valid=1
                else:
                    i=r
                    valid=0
            else:
                #print(i,i+k-1,"..",(i+k-2)%n,(i+k-1)%n)
                if colors[(i+k-2)%n]!=colors[(i+k-1)%n]:
                    #print(i,i+k-1,"..")
                    cnt+=1
                    i+=1
                    #print(".",i)
                else:
                    valid=0
                    i+=k-1
        #print("...")
        return cnt
