class Solution {
public:
int numberOfAlternatingGroups(vector<int> &a,int k)
{
    int n=a.size(),ans=0;
    bool found=false;
    for(int i=0;i<n;++i)
    {
        if(a[i]==a[(i+1)%n]||(a[i]==a[(i-1+n)%n]))
        {
            found=true;
            break;
        }
    }
    if(!found)
    {
        return n;
    }
    vector<int> idx;
    idx.push_back(0);
    for(int i=0;i<n-1;++i)
    {
        if(a[i]==a[i+1])
        {
            idx.push_back(i);
            idx.push_back(i+1);
        }
    }
    idx.push_back(n-1);
    for(int i=0;i<idx.size();i+=2)
    {
        int cur=idx[i+1]-idx[i]+1;
        ans+=max(0,cur-k+1);
    }
    if(a[0]!=a.back())
    {
        int i1=-1,i2=-1;
        for(int i=n-1;i>0;--i)
        {
            if(a[i]==a[i-1])
            {
                i2=i;
                break;
            }
            if(n-i+1>=k)
            {
                i2=i;
                break;
            }
        }
        for(int i=0;i<n-1;++i)
        {
            if(a[i]==a[i+1])
            {
                i1=i;
                break;
            }
            if(i+2>=k)
            {
                i1=i;
                break;
            }
        }
        if(i1!=-1&&i2!=-1)
        {
            int cur=i1+1+n-i2;
            ans+=max(0,cur-k+1);
        }
    }
    return ans;
}
};
