class Solution {
public:
    // baingan is the owner of this code
    // written on 1-07-2024
    int numberOfAlternatingGroups(std::vector<int>& cols, int len) {
        cols.insert(cols.end(), cols.begin(), cols.begin() + len);

        int n = cols.size(), count = 0;
        std::vector<int> diff(n + 1, 0);

        for (int i = 1; i < n; ++i) {
            diff[i] += diff[i - 1] + std::abs(cols[i] - cols[i - 1]);
        }

        len--;
        for (int i = len + 1; i < n; ++i) {
            if (diff[i] - diff[i - len] == len) {
                count++;
            }
        }

        return count;
    }
};
