class Solution {
public:
   int numberOfAlternatingGroups(std::vector<int>& arr, int k) {
    int num_found = 0;

       // author atul 
    int mickey = 0, minnie = 1;


       map<int,int>mp ;




    int pluto = arr.size();

    while (minnie < pluto + k && mickey < pluto) {

        int goofy_j = minnie;



        int goofy_end = goofy_j % pluto;

        while (arr[goofy_end] != arr[(goofy_end - 1 + pluto) % pluto] && goofy_j < (pluto + k - 1)) {


            goofy_j++;


            goofy_end = goofy_j % pluto;


        }

        num_found += std::max(goofy_j - mickey - k + 1, 0);



        mickey = goofy_j;

            mp[mickey] ++ ;



        minnie = mickey + 1;

    }

       for(auto it : mp){
           cout<<it.first<<" " ;
       }

    return num_found;
}

};
