class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int cnt = 0;
        int n = colors.size();
        int i=0;
        int j=1;
        int pos = -1;
        while (i < n) {
            while (j-i < k) {
                if (colors[(j-1)%n] == colors[j%n]) {
                    pos = j;
                }
                j++;
            }
            if (pos <= i) {
                cnt++;
            }
            i++;
        }
        return cnt;
    }
};
