#include <vector>
using namespace std;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        return cA(colors, k);
    }
    int cA(vector<int>& colors, int k) {
        int n = colors.size();
        if (k==1) return n;

        int ans = 0;
        int alternatingLength = 1;

        for (int i = 1; i <n+k-1 ; i++) {
            int current = i % n;
            int previous = (i-1)%n;

            if (colors[current] != colors[previous]) {
                alternatingLength++;
            } else {
                alternatingLength = 1;
            }

            if (alternatingLength >= k) {
                ans++;
            }
        }

        return ans;
    }
};
