class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int ans = 0;
        vector<int> arr;
        for(int i=0; i<n-1; i++){
            if(colors[i] == colors[i+1]){
                arr.push_back(i);
            }
        }
        if(arr.empty()){
            ans += n-k+1;
            if(colors[n-1] != colors[0]){
                ans += k-1;
            }
            return ans;
        }
        for(int i=1; i<arr.size(); i++){
            int sz = arr[i]-arr[i-1];
            ans += max(0, sz-k+1);
        }
        int m = arr.size();
        if(colors[n-1] != colors[0]){
            int sz = arr[0]+1+n-arr[m-1];
            ans += max(0, sz-k);
        }else{
            int sz1 = arr[0]+1;
            ans += max(0, sz1-k+1);
            ans += max(0, n-arr[m-1]-k);
        }

        return ans;
    }
};
