class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        colors+=colors[:k-1]
        n=len(colors)
        s=ans=0
        for i in range(n-1):
            s+=colors[i]^colors[i+1]
            if i-k+1>=0:s-=colors[i-k+1]^colors[i-k+2]
            ans+=s==k-1
        return ans
