class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {

        int ret = 0;
        int i = 0;
        int j = 1;
        int count = 0;

        while (j < arr.size() + k && i < arr.size()) {
            int pointj = j;
            int pointend = pointj % arr.size();

            while (arr[pointend] !=
                       arr[(pointend - 1 + arr.size()) % arr.size()] &&
                   (pointj < (arr.size() + k - 1))) {
                pointj += 1;
                pointend = pointj % arr.size();
            }

            count += max(pointj - i - k + 1, 0);
            i = pointj;
            j = i + 1;
        }

        return count;
    }
};
