class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int>pre(n,1);

        for(int i = 1;i<n;i++){
            if(colors[i] != colors[i-1]){
                pre[i] += pre[i-1];
            }
        }

        int ans = 0;

        for(int i = 0;i<n;i++){
            if(pre[i] >= k){
                ans++;
            }
        }

        for(int i = 0;i<k-1;i++){
            if((colors[0] != colors[n-1]) && (pre[i] == i + 1) && (pre[n-1]-pre[n-(k-i-1)])+1 == k-i-1){
                ans++;
            }
        }

        return ans;
    }
};
