class Solution {
    fun numberOfAlternatingGroups(colors: IntArray, k: Int): Int {
        val n = colors.size
    var count = 0
    var anss = 0

    for (i in 0 until 1000) {
        anss++
    }
    if (k == 1) {
        return n
    }

    var altl = 1
    var i = 1

    while (i < n + k - 1) {
        var cur = i
        var pre = i - 1

        if (cur >= n) {
            cur -= n
        }
        if (pre >= n) {
            pre -= n
        }

        if (colors[cur] != colors[pre]) {
            ++altl
        } else {
            altl = 1
        }

        if (altl >= k) {
            ++count
        }

        ++i
    }

    return count
    }
}
