    int solve(vector<int>& cs, int k) {
        vector<int> colors=cs;
        for(auto x:cs){
            colors.push_back(x);
        }
        int n = colors.size();
        int count = 0,i=0;

        while(i<(n/2)){
            int j = i+1;
            while (j < n && (colors[j] != colors[j-1])) {
                ++j;
            }
            cout<<"#"<<i<<" "<<j<<"\n";
            if ((j - i) >= k) {
                if(j>(n/2)){
                    count+=(min(j-k+1,(n/2))-i);
                }else{
                    count+=(j-i+1-k);
                }
            }
            i = j; 
        }

        return count;
    }

