class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int i=0;
        int j=0;
        int n=a.size();
        bool s=false;
        int ans=0;
        while(i!=n){
            int t=j;
            j++;
            if(j==n){
                j=0;
                s=true;
            }
            if(a[j]==a[t]){
                if(s) break;
                i=j;
                continue;
            }
            int siz;
            if(s){
                siz=(n-i)+j+1;
            }else{
                siz=j-i+1;
            }
            if(siz==k){
                ans++;
                i++;
            }
        }
        return ans;
    }
};
