class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int i,j,m,n=colors.size(),ans =0;
        for(i=0;i<k-1;++i) colors.push_back(colors[i]);
        i=0; m = colors.size();
        while(i<n){
            for(j=0;j<k;++j){
                if (j%2==0 && colors[i] != colors[j+i]) break;
                if (j%2==1 && colors[i] == colors[j+i]) break;
            }
            if (j!=k){
                i+=j;
            }else{
                ++ans;
                j=i+k;
                while(j<m && colors[j] != colors[j-1]){
                    ++ans;++j;
                }
                i=j;
            }
        }
        return ans;
    }
};
