class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int r = 0;
        int n = c.size();
        int ans = 0;
        for(int i = 0; i < n; i++){
            if(r < i){
                r = i;
            }
            while(c[(r + 1) % n] != c[r % n] && r - i + 1 < k) {
                r ++;
            }
            if(r - i + 1 >= k){
                ans ++;
            }
        }
        return ans;
    }
};
