class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int i, n = a.size(), j, ans = 0;
        for (i = 0; i < k - 1; i++)
            a.push_back(a[i]);
        n = a.size();
        i =1, j = 0;
        while (i < n) {
            if (a[i] == a[i - 1])
                j = i;
            else {
                if (i - j + 1 == k)
                    ans++, j++;
            }
            i++;
        }
        return ans;
    }
};
