class Solution {
public:
typedef long long ll;
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        ll n = a.size();
        vector<ll> dp(n);
        dp[0]=0;
        for(int i=1; i<n; i++){
            if(a[i]==a[i-1]){
                dp[i]=1+dp[i-1];
            }else dp[i]=dp[i-1];
        }
        ll res=0;
        ll l=0;
        for(int i=k-1; i<n; i++){
            ll cnt = dp[i] - dp[l];
            if(cnt==0) res++;
            l++;
        }

        int g =0;
        if(a[0]==a[n-1]) g++;

        for(int i=n-k+1; i<n; i++){
            int r = (i+k-1)%n;
            ll cnt =  (dp[n-1] - dp[i]) + (dp[r]-dp[0]) + g;
            if(cnt==0) res++;
        }

        return res;
    }
};
