class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {

        int[] arr = new int[colors.length + (k-1)];
        for(int i=0;i<colors.length;++i)
        {
            arr[i] = colors[i];
        }
        int n = arr.length;
        for(int i=0;i<k-1;++i)
        {
            arr[n-(k-1-i)] = colors[i];
        }

        int ans=0;

        int i=0, j=0;
        while(i<n)
        {
            int start = arr[i];
            j=i+1;
            if(j==n)
            {
                break;
            }
            while(j<n && (arr[j-1] + arr[j]) == 1)
            {
                j++;
            }

            if(j-i >= k)
            {
                ans+=(j-i-k+1);
            }
            i=j;

        }

        return ans;

    }
}
