class Solution {
public:
    bool check(vector<int>& colors,int i) {
        int l=i-1,r=i+1;
        if(colors[l]!=colors[i] && colors[r]!=colors[i])
            return true;
        return false;
    }
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size(),ct = 0, grp = 2;
        colors.insert(colors.end(),colors.begin(),colors.end());
        int l=0,r=1;
        while(l<n)
        {
            if(check(colors,r))
            {
                r++;
                grp++;
            }
            else
            {
                l=r;
                r=l+1;
                grp=2;
            }
            if(grp==k)
            {
                ct++;
                l++;
                grp--;
            }
        }
        return ct;
    }
};
