class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {

        int n = arr.size();
        vector<int>v(n+k-1);

        for(int i=0;i<n;i++) v[i] = arr[i];



        int cnt =0;

        bool ok = true;

        for(int i =0;i<k-1;i++){
           v[n+i] = arr[i];
        }

        int N = 1;


        for(int i = 1;i<v.size();i++){
           if(v[i]!=v[i-1]){
               ++N;
           }else N=1;
            if(N>=k) cnt++;
        }
        return cnt;
    }
};
