class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        if (k == 1) {
            return n;
        }
        vector<int> arr(n);
        for (int i = 0; i < n; ++i) {
            if(colors[i]!=colors[(i+1)%n]){
                arr[i]=1;
            }
            else{
                arr[i]=0;
            }
        }
        int sum = 0;
        for (int i = 0; i < k - 1; ++i) {
            sum+=arr[i];
        }
        int cnt=0;
        for (int i = 0; i < n; ++i) {
            if (sum==k - 1) {
                cnt++;
            }
            sum-=arr[i];
            sum+=arr[(i+k-1)%n];
        }
        return cnt;
    }
};
