class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int ans = 0;
        int alternate = 0;
        for (int i = 0; i < k - 1; ++i) {
            if (colors[i] != colors[i + 1]) {
                alternate++;
            }
        }
        if (alternate == k - 1) {
            ans++;
        }
        for (int i = k; i < n + k - 1; ++i) {
            if (colors[(i - k) % n] != colors[(i - k + 1) % n]) {
                alternate--;
            }
            if (colors[(i - 1) % n] != colors[i % n]) {
                alternate++;
            }
            if (alternate == k - 1) {
                ans++;
            }
        }
        return ans;
    }
};
