class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        for i in range(k-1): colors.append(colors[i])
        ans = 0
        n = len(colors)
        dp = [1]*n
        for i in range(1, n):
            if colors[i]!=colors[i-1]: dp[i] = dp[i-1] + 1
            if dp[i]>=k: ans += 1
        #print(dp)
        return ans
