class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:

        len_colors=len(colors)
        colors = colors+colors

        @cache
        def is_alt(i):
            if i>0 and is_alt(i-1):
                return colors[i+k-1]!=colors[i+k-2]


            for idx in range(i, i+k-1, 1):
                if colors[idx]==colors[idx+1]:
                    return False


            return True

        res = 0
        for i in range(len_colors):
            if is_alt(i):
                res+=1
        return res
