class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int> last(n);
        for(int i = 1;i < n;i++) {
            if(colors[i]!=colors[i-1]) {
                last[i] = last[i-1];
            } else {
                last[i] = i;
            }
        }  
        int ans = 0;
        for(int i = 0;i < n;i++) {
            int j = i+k-1;
            if(j >= n) {
                if(colors[0]!=colors[n-1]) {
                    j%=n;
                    if(last[j]<=0&&last[n-1]<=i)ans++;
                }
            } else {
                int l = last[j];
                if(l<=i) ans++;
            }
        }
        return ans;
    }
};
