class Solution {
public:
int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int nFound = 0;
        int i = 0;
        int j = 1;
        int n = colors.size();

        while (j < n + k && i < n) {
            int tempJ = j;
            int tEnd = tempJ % n;

            // Find the end of the current alternating group
            while (colors[tEnd] != colors[(tEnd - 1 + n) % n] && tempJ < n + k - 1) {
                tempJ++;
                tEnd = tempJ % n;
            }

            // Calculate the number of groups found
            nFound += max(tempJ - i - k + 1, 0);
            i = tempJ;
            j = i + 1;
        }

        return nFound;
    }
};
