class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k){
        vector<int>pp;
        for(int i = 0; i<colors.size(); i++){
            pp.push_back(colors[i]);
        }
        for(int i = 0; i<k; i++){
            pp.push_back(colors[i]);
        } 
        vector<int>v(pp.size()+1, 0);
        for(int i = 1; i<pp.size(); i++){
            v[i] += v[i-1]+abs(pp[i]-pp[i-1]);
        }
        int ans = 0;
        for(int i = k; i<pp.size(); i++){
            if(v[i] - v[i-k+1] == k-1){
                ans++;
            }
        }
        return ans;
    }
};
