class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int j=1;
        int c=k-1;
        int last=colors[0];
        int n=colors.size();
        int count=0;
        while(j<n+k-1){

            while(last!=colors[j%n] && j<n+k-1){
                c--;
                if(c<=0){
                    count++;
                }

                last=colors[j%n];
                j++;

            }
            c=k-1;
            last=colors[j%n];
            j++;
        }
        return count;
    }
};
