class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> a;
        int ans=0;
        for(int i=0;i<colors.size();i++){
            a.push_back(colors[i]);
        }
        for(int i=0;i<k-1;i++){
            a.push_back(colors[i]);
        }
        for(int i=0;i<a.size();i++){
            int cnt=1;
            for(int j=i+1;j<a.size();j++){
                if(a[j]!=a[j-1]){
                    cnt++;
                }
                else{
                    i=j-1;
                    break;
                }
                if(j==a.size()-1){
                    i=3*a.size();
                    break;
                }
            }
            ans+=max(0,cnt-k+1);
        }
        return ans;
    }
};
