class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& col, int k) {
        int n=col.size();
        int ans=0;
        int alter=1;
        for(int i=0;i<n+k-2;i++){
            if(col[(i)%n]!=col[(i+1)%n]) alter++;
            else{
                if(alter>=k){
                    ans+=alter-k+1;
                }
                alter=1;
            }
            // cout<<alter<<endl;
        }
        if(alter>=k){
         ans+=alter-k+1;
       }

        return ans;
    }
};
