class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        vector<int>v;
        for(auto it:colors){
            v.push_back(it);
        }
        for(auto it:colors){
            v.push_back(it);
        }
        // for(auto it:v){
        //     cout<<it<<" ";
        // }
        // cout<<endl;
        int ct=0;
        long long ans=0;

       for(int i=0;i<k;i++){
         if(i>0){
            if(v[i]==v[i-1]){
                ct++;
            }
         }
       }
       if(ct==0) ans++;
       for(int i=k;i<2*n&&(i-k+1<n);i++){
         if(v[i-k]==v[i-k+1]){
            ct--;
         }
         if(v[i]==v[i-1]) ct++;
         if(ct==0) ans++;
       //  cout<<ct<<" ";
       }
       return ans;

    }
};
