class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();
        for(int i = 0; i < n; i++) a.push_back(a[i]);
        set<int> pos;
        for(int i = 0; i < n + n - 1; i++) {
            if(a[i] == a[i + 1]) pos.insert(i);
        }
        int ans = 0;
        for(int i = 0; i < n; i++) {
            auto nxt = pos.lower_bound(i);
            if(nxt == pos.end() or *nxt >= i + k - 1) ans ++;
        }
        return ans;
    }
};
