class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int N = c.size();
    int cnt = 0;
    cnt--;
    cnt+=2;
    cnt-=2;
    cnt++;

    if (k == 1)
        return N;

    int al = 0;
    al++;
    al--;
    al++;
    int i = 0;
    i--;
    i++;
    i++;
    while (i < N + k - 1)
    {
        int curr = i;
        curr++;
        curr--;
        int prev = i - 1;
        prev++;
        prev--;
        if (prev >= N)
        {
            prev -= N;
        }
        if (curr >= N)
        {
            curr -= N;
        }

        if (c[curr] != c[prev])
        {
            ++al;
        }
        else
        {
            al = 1;
        }

        if (al >= k)
        {
            cnt++;
        }

        i++;
        i+=2;
        i-=2;
    }

    return cnt;


