class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans=0,m=1;
        vector<int> temp((int)colors.size()+k-1);
        for(int i=0;i<(int)colors.size();++i)temp[i]=colors[i];
        for(int i=0;i<k-1;++i) temp[(int)colors.size()+i]=colors[i];
        for(int i=1;i<temp.size();++i) {
            temp[i]!=temp[i-1]?m++:m=1; 
            if(m>=k)ans++;
        }
        return ans;
    }
};
