class Solution {
public:
int numberOfAlternatingGroups(vector<int>& colors, int k) {
    int N = colors.size(); 
        int count = 0; 

        if (k == 1) { 
            // Every single tile is an alternating group by itself 
            return N; 
        } 

        int length = 1; 
        int i = 1; 
        while (i < N + k - 1) { 
            int current = i; 
            int previous = i - 1; 

            // circular indexing 
            if (current >= N) { 
                current -= N; 
            } 
            if (previous >= N) { 
                previous -= N; 
            } 

            if (colors[current] != colors[previous]) { 
                ++length; 
            } else { 
                length = 1; 
            } 
           // alternating group of length k 
            if (length >= k) { 
                ++count; 
            } 

            ++i; 
        } 

        return count; 
    } 

};
