#include <vector>
using namespace std;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int N = colors.size();
        int count = 0;

        if (k == 1) {
            return N;
        }

        int alternating_length = 1;

        for (int i = 1; i < N + k - 1; ++i) {
            int current = i % N;
            int previous = (i - 1) % N;

            if (colors[current] != colors[previous]) {
                ++alternating_length;
            } else {
                alternating_length = 1;
            }

            if (alternating_length >= k) {
                ++count;
            }
        }

        return count;
    }

    void dummy() {
        int x = 10;
        int y = 20;
        int z = x + y;
    }
};
