class Solution { 
public: 
    int numberOfAlternatingGroups(vector<int>& colors, int k) { 
        int sizeVal = colors.size(),cV = 0,iV=1; 
        if (k == 1) {  
            return sizeVal; 
        } 
        int alV = 1;  
        while (iV < sizeVal + k - 1) { 
            int cuV = iV; 
            int pV = iV - 1; 
            if (pV >= sizeVal) { 
                pV -= sizeVal; 
            }if (cuV >= sizeVal) { 
                cuV -= sizeVal; 
            }if (colors[cuV] != colors[pV]) { 
                alV++; 
            }else{ 
                alV = 1; 
            } 
            if (alV >= k) { 
                cV++; 
            } 
            iV++; 
        } 
        return cV; 
    } 
};
