class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int numFound = 0;
        int n = colors.size();
        int i = 0;
        int j = 1;

        while (j < n + k && i < n) {
            int temp_j = j;
            int temp_end = temp_j % n;

            while (colors[temp_end] != colors[(temp_end - 1 + n) % n] && (temp_j < n + k - 1)) {
                temp_j++;
                temp_end = temp_j % n;
            }

            numFound += max(temp_j - i - k + 1, 0);
            i = temp_j;
            j = i + 1;
        }

        return numFound;
    }
};
